        public String cfReferenceQueueNo
        {
            get
            {
                string referenceQueueNoInString = _ReferenceQueueNo.ToString();
                string referenceQueueNoInitial1 = referenceQueueNoInString.Substring(0, 1);
                string referenceQueueNoInitial2 = referenceQueueNoInString.Substring(1);
                string referenceQueueNo = "";

                if (referenceQueueNoInitial1 == "1")
                {
                    referenceQueueNo = referenceQueueNoInitial1.Replace("1", "B");
                    referenceQueueNo = string.Format("{0}{1}", referenceQueueNo, referenceQueueNoInitial2);
                }
                else if (referenceQueueNoInitial1 == "2")
                {
                    referenceQueueNo = referenceQueueNoInitial1.Replace("2", "A");
                    referenceQueueNo = string.Format("{0}{1}", referenceQueueNo, referenceQueueNoInitial2);
                }
                else
                {
                    referenceQueueNo = "";
                }

                return referenceQueueNo;
            }
        }

