                if (_PlanDischargeNotes != string.Empty)
                {
                    if (concNotes.Length > 100)
                    {
                        concNotes = string.Format("({0} - {1})", _PlanDischargeNotesType, _PlanDischargeNotes.Substring(0, 10) + "...");
                    }
                }
                else
                {
                    concNotes = string.Format("({0})", _PlanDischargeNotesType);
                }

                if (_GCVisitStatus == Constant.StandardCode.RegistrationStatus.PAID || _GCVisitStatus == Constant.StandardCode.RegistrationStatus.CLOSED)
                {
                    if (_DischargeDate.ToString(Constant.FormatString.DATE_FORMAT) != Constant.ConstantDate.DEFAULT_NULL_DATE_FORMAT)
                    {
                        notes = string.Format("Tanggal Pulang = {0}", _DischargeDate.ToString(Constant.FormatString.DATE_FORMAT));
                    }
                    else
                    {
                        if (_IsPlanDischarge)
                        {
                            if (_PlanDischargeDate.ToString(Constant.FormatString.DATE_FORMAT) != Constant.ConstantDate.DEFAULT_NULL_DATE_FORMAT)
                            {
                                notes = string.Format("Rencana Pulang = {0} {1}", _PlanDischargeDate.ToString(Constant.FormatString.DATE_FORMAT), concNotes);
                            }
                            else
                            {
                                notes = "";
                            }
                        }
                        else
                        {
                            notes = "";
                        }
                    }
                }
                else
                {
                    if (_IsPlanDischarge)
                    {
                        if (_PlanDischargeDate.ToString(Constant.FormatString.DATE_FORMAT) != Constant.ConstantDate.DEFAULT_NULL_DATE_FORMAT)
                        {
                            notes = string.Format("Rencana Pulang = {0} {1}", _PlanDischargeDate.ToString(Constant.FormatString.DATE_FORMAT), concNotes);
                        }
                        else
                        {
                            notes = "";
                        }
                    }
                    else
                    {
                        notes = "";
                    }
                }
                return notes;
