                    DateTime fromDateTime = new DateTime(_VisitDate.Year, _VisitDate.Month, _VisitDate.Day, fromHour, fromMinute, 0);
                    DateTime toDateTime = new DateTime(_StartServiceDate.Year, _StartServiceDate.Month, _StartServiceDate.Day, toHour, toMinute, 0);
                    waitingTimeInMinutes = (fromDateTime - toDateTime).TotalMinutes;
