            {
                double waitingTimeInMinutes = 0;

                if (_GCVisitStatus == "X020^001" || _GCVisitStatus == "X020^002")
                {
                    int minute = Convert.ToInt32(_VisitTime.Substring(3));
                    int hour = Convert.ToInt32(_VisitTime.Substring(0, 2));
                    DateTime fromDateTime = DateTime.Now;
                    DateTime toDateTime = new DateTime(_VisitDate.Year, _VisitDate.Month, _VisitDate.Day, hour, minute, 0);
                    waitingTimeInMinutes = (fromDateTime - toDateTime).TotalMinutes;
                }
                else
                {
                    int fromMinute = Convert.ToInt32(_VisitTime.Substring(3));
                    int fromHour = Convert.ToInt32(_VisitTime.Substring(0, 2));
                    int toMinute = 0;
                    int toHour = 0;
                    if (!string.IsNullOrEmpty(_StartServiceTime))
                    {
                        toMinute = Convert.ToInt32(_StartServiceTime.Substring(3));
                        toHour = Convert.ToInt32(_StartServiceTime.Substring(0, 2));
                    }
                    DateTime fromDateTime = new DateTime(_VisitDate.Year, _VisitDate.Month, _VisitDate.Day, fromHour, fromMinute, 0);
                    DateTime toDateTime = new DateTime(_StartServiceDate.Year, _StartServiceDate.Month, _StartServiceDate.Day, toHour, toMinute, 0);
                    waitingTimeInMinutes = (fromDateTime - toDateTime).TotalMinutes;
                }

                if (waitingTimeInMinutes <= 45)
                    return 4;
                if (waitingTimeInMinutes <= 90)
                    return 3;
                if (waitingTimeInMinutes <= 120)
