        public string cfWaitingTime
        {
            get
            {
                if (_GCVisitStatus == Constant.StandardCode.RegistrationStatus.CHECKED_IN)
                {
                    int minute = Convert.ToInt32(_VisitTime.Substring(3));
                    int hour = Convert.ToInt32(_VisitTime.Substring(0, 2));
                    DateTime VisitDateTime = new DateTime(_VisitDate.Year, _VisitDate.Month, _VisitDate.Day, hour, minute, 0);
                    double totalHours = (DateTime.Now - VisitDateTime).TotalHours;

                    double hours = Math.Floor(totalHours);
                    double minutes = Math.Floor((totalHours - hours) * 60);

                    return string.Format("{0}:{1}", hours.ToString().PadLeft(2, '0'), minutes.ToString().PadLeft(2, '0'));
