                }

                total = transAmount - finalDiscAmount + vatAmount;

                return total;
            }
        }

        public string cfTotalDirectPurchaseInString
        {
            get
            {
                decimal total = 0;
                decimal transAmount = _TransactionAmount;
                decimal finalDiscInPercentage = _FinalDiscount;
                decimal finalDiscAmount = _FinalDiscountAmount;
                decimal vatPercentage = _VATPercentage;
                decimal vatAmount = 0;

                if (_FinalDiscount > 0)
                {
                    finalDiscAmount = Convert.ToDecimal(transAmount * finalDiscInPercentage / 100);
                }

                if (_IsIncludeVAT)
                {
                    vatAmount = Convert.ToDecimal((transAmount - finalDiscAmount) * vatPercentage / 100);
                }

                total = transAmount - finalDiscAmount + vatAmount;

                return total.ToString(Constant.FormatString.NUMERIC_2);
            }
        }
