                {
                    string[] names = _PatientName.Split(' ');

                    if (names.Length >= 3)
                        result = string.Format("{0} {1} {2}", names[0], names[1], names[2]);

                    if (result.Length > 25)
                        result = result.Substring(0, 25);

                    string _salutation = string.Empty;
                    if (_PatientName.Contains(','))
                    {
                        string[] temp = _PatientName.Split(',');
                        if (temp.Length >= 2)
                        {
                            _salutation = string.Format(", {0}", temp[1]);
                        }
                    }
                    result = string.Format("{0} {1}", result.Replace(",", ""), _salutation);
                }

                return result;
            }
