                    int hour = Convert.ToInt32(_VisitTime.Substring(0, 2));
                    DateTime VisitDateTime = new DateTime(_VisitDate.Year, _VisitDate.Month, _VisitDate.Day, hour, minute, 0);
                    double totalHours = (DateTime.Now - VisitDateTime).TotalHours;

                    double hours = Math.Floor(totalHours);
