        {
            get { return _DateOfBirth.ToString(Constant.FormatString.DATE_FORMAT); }
        }
        public string cfPatientAge
        {
            get
            {
                if (_DateOfBirth.ToString("dd-MM-yyyy") == Constant.ConstantDate.DEFAULT_NULL)
                    return "";
                else
                {
                    string result = "";

                    if (_AgeInYear <= 0 && _AgeInMonth <= 0 && _AgeInDay <= 0)
                    {
                        result = "-";
                    }
                    else if (_AgeInYear <= 0 && _AgeInMonth <= 0 && _AgeInDay > 0)
                    {
                        result = string.Format("{0}day", _AgeInDay);
                    }
                    else if (_AgeInYear <= 0 && _AgeInMonth > 0 && _AgeInDay <= 0)
                    {
                        result = string.Format("{0}mo", _AgeInMonth);
                    }
