        public int PatientSatisfactionLevel
        {
            get
            {
                int minute = Convert.ToInt32(_RegistrationTime.Substring(3));
                int hour = Convert.ToInt32(_RegistrationTime.Substring(0, 2));
                DateTime VisitDateTime = new DateTime(_RegistrationDate.Year, _RegistrationDate.Month, _RegistrationDate.Day, hour, minute, 0);
                double WaitingTimeInMinutes = (DateTime.Now - VisitDateTime).TotalMinutes;

                if (WaitingTimeInMinutes <= 45)
                    return 4;
                if (WaitingTimeInMinutes <= 90)
                    return 3;
                if (WaitingTimeInMinutes <= 120)
                    return 2;
                return 1;

            }
        }

