                DateTime VisitDateTime = new DateTime(_VisitDate.Year, _VisitDate.Month, _VisitDate.Day, hour, minute, 0);
                double WaitingTimeInMinutes = (DateTime.Now - VisitDateTime).TotalMinutes;

                if (WaitingTimeInMinutes <= 45)
                    return 4;
                if (WaitingTimeInMinutes <= 90)
                    return 3;
                if (WaitingTimeInMinutes <= 120)
                    return 2;
                return 1;

            }
        }
