                    DateTime ccDate = new DateTime(_StartServiceDate.Year, _StartServiceDate.Month, _StartServiceDate.Day, Convert.ToInt16(timeInfo[0]), Convert.ToInt16(timeInfo[1]), 0);
                    TimeSpan diff = DateTime.Now - ccDate;
                    double hours = diff.TotalHours;

                    return (hours <= 24);
                }
                else
                {
                    TimeSpan diff = DateTime.Now - DateTime.Now;
                    double hours = diff.TotalHours;

                    return (hours <= 24);
                }
            }
        }
    }
