        {
            get
            {
                if (_GCBeamTechnique == "X572^999")
                {
                    StringBuilder sbText = new StringBuilder();

                    sbText.AppendLine(_BeamTechniqueName);
                    string[] combinationInfo = _CombinationBeamTech.Split(';');
                    foreach (string techName in combinationInfo)
                    {
                        sbText.AppendLine(string.Format("- {0}", techName));
                    }
                    return sbText.ToString();
                }
                else
                {
                    return _BeamTechniqueName;
                }
            }
        }

        public List<string> cfCombinationBeamTechniqueCode
        {
            get
            {
                if (_GCBeamTechnique == "X572^999")
                {
                    List<string> lstCode = new List<string>();

                    string[] combinationInfo = _CombinationBeamTechCode.Split(';');
                    foreach (string techName in combinationInfo)
                    {
                        lstCode.Add(techName);
                    }
                    return lstCode;
                }
                else
                {
                    return null;
                }
            }
        }

        public Int32 cfTotalFraction
        {
            get
            {
                decimal _total = _NumberOfFields1;

                if (_IsHasDosage2)
                    _total += _NumberOfFields2;

                if (_IsHasDosage3)
                    _total += _NumberOfFields3;

                return Convert.ToInt32(_total);
            }
        }

        public string cfDosageInfo
        {
            get
            {
                string info = _NumberOfDosage1.ToString();

                if (_IsHasDosage2)
                {
                    StringBuilder dosageInfo = new StringBuilder();
                    dosageInfo.AppendLine(string.Format("Dosis-1 : {0}", _NumberOfDosage1.ToString()));
                    dosageInfo.AppendLine(string.Format("Dosis-2 : {0}", _NumberOfDosage2.ToString()));

                    info = dosageInfo.ToString();
                }
                if (_IsHasDosage2)
                {
                    StringBuilder dosageInfo = new StringBuilder();
                    dosageInfo.AppendLine(string.Format("Dosis-1 : {0}", _NumberOfDosage1.ToString()));
                    dosageInfo.AppendLine(string.Format("Dosis-2 : {0}", _NumberOfDosage2.ToString()));
                    dosageInfo.AppendLine(string.Format("Dosis-3 : {0}", _NumberOfDosage3.ToString()));

                    info = dosageInfo.ToString();
                }
                return string.Format("{0} Gy", info);
            }
        }

